function get_access_level($staff_id) {
    //echo("staff_id is " . $staff_id);
    $mysqli = create_mysqli();
    if (mysqli_connect_errno()) {
        //$output = ("Connect failed.");
        exit();
    }
    $stmt = $mysqli->prepare("SELECT a.access_lvl FROM staff s JOIN position p USING (position_id) JOIN application_access a USING (access_grp) WHERE s.active_flag = 'T' AND staff_id = ?");
    //print_r($stmt);
    $stmt->bind_param('s', $staff_id);
    $stmt->execute();
    $stmt->store_result();
    if ($stmt->num_rows != 1) {
        return false; 
    }
    else
    {
        $stmt->bind_result($access_lvl);
        $stmt->fetch();
        $stmt->close();
        return $access_lvl;
    }
}

