if (context->err) {
    fprintf(stderr,"Could not connect to Redis at ");
    if (config.hostsocket == NULL)
        fprintf(stderr,"%s:%d: %s\n",config.hostip,config.hostport,context->errstr);
    else
        fprintf(stderr,"%s: %s\n",config.hostsocket,context->errstr);
    redisFree(context);
    context = NULL;
    return REDIS_ERR;
}

